
public class Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	  int x=1;
		int y=2;
		int z;
		int i=0;
		
		
		z=x+y;
		
		System.out.println(z);
		
	++x;
	
	z=x+y;
	
	System.out.println(z);
	System.out.println("*******************************************************************");
	for(i=0; i<=8; i++){
		System.out.println("Counter is:");
		System.out.println(i);
	}
	if(i>10){
		System.out.printf("The counter is bigger than 10");
	}

	else
		System.out.println("The counter is not bigger than 10");

}
}